mapper.select:
SELECT d.d_no, d.loginID, d.d_title, d.d_weather, dc_mood.detail_name AS d_mood, d.d_contents, d.d_diarydt, d.d_regdate FROM tb_diary d JOIN tb_detail_code dc_mood ON d.d_mood = dc_mood.detail_code AND dc_mood.group_code = '1' WHERE d.loginID = #{userId}
and d.d_title Like CONCAT('%', #{title}, '%')
ORDER BY d_diarydt DESC

mapper.insert:

            SELECT IFNULL(MAX(d_no),0) + 1 FROM tb_diary 

		INSERT
		INTO		tb_diary
				(
					d_no, loginID, d_title, d_weather, d_mood, d_contents, d_diarydt, d_regdate
				)
		VALUES
				(
						#{d_no}
					,	#{loginId}
					,	#{d_title}
					,	#{d_weather}
					,	#{d_mood}
					,	#{d_contents}
					,	#{d_diarydt}
					,	NOW()
				)
